mapper.update:
update quest_router
route_name = #{routeName}, begin_name = #{beginName}, end_name = #{endName}, status = #{status}, image_url = #{imageUrl}, core = #{core}, flag = #{flag}, create_date = #{createDate}, update_date = #{updateDate},
where router_id = #{routerId}

mapper.select:
SELECT d.router_id, c.problem_type, c.type_weight, c.problem, c.answer_type, c.garde, c.issue_id, d.avgGrade FROM quest_question c,( SELECT a.router_id router_id, a.issue_id issue_id, AVG(CAST(a.answer AS SIGNED)) AS avgGrade, count(*) total FROM quest_question b INNER JOIN quest_answer a ON b.issue_id = a.issue_id
b.answer_type = 1 and a.router_id = #{routerId}
GROUP BY a.router_id, a.issue_id ORDER BY a.router_id ) d WHERE c.issue_id = d.issue_id
